import React from "react";
import DeleteIcon from '@mui/icons-material/Delete';

function Note(props) {

  var startStatus = "more";

  function toggleText() {
    var text =(props.content);


      if (startStatus == "less") {
        document.getElementById("textArea").innerHTML = `${text.substring(0, 12)}...`;
        document.getElementById("toggleButton").innerText = "More";
        startStatus = "more";
      } else if (startStatus == "more") {
        document.getElementById("textArea").innerHTML = text;
        document.getElementById("toggleButton").innerText = "Less";
        startStatus = "less";
      }
     else {
      document.getElementById("textArea").innerHTML = text;
    }
  }
  function handleClick(){
    props.onDelete(props.id);
  }


  return (
    <div className="note">
      <h1>{props.title}</h1>
      <p id="textArea" className="line-clamp-4">{props.content}</p>
      <button id="toggleButton" onClick={toggleText}></button>
      <button onClick={handleClick}><DeleteIcon /></button>
    </div>
  );
}



export default Note;
